import React, { useEffect, useState } from 'react';
import { DayPilotCalendar, DayPilot } from "@daypilot/daypilot-lite-react"; // Import DayPilot
import "./Calendar.css";

const styles = {
  wrap: {
    display: "flex"
  },
  main: {
    flexGrow: "1"
  }
};

const Calendar = () => {
  const [calendar, setCalendar] = useState(null);
  const [events, setEvents] = useState([]);

  // Fixed start date to always show the same week (Monday to Friday)
  const startDate = "2024-10-07"; // Monday of the desired week

  const config = {
    viewType: "days",
    durationBarVisible: false,
    days: 5, // Show only Monday to Friday
    timeRangeSelectedHandling: "Enabled",
    businessBeginsHour: 7,
    businessEndsHour: 20,
    onTimeRangeSelected: async args => {
      // Base reference date: 07/10/2024 is Monday
      const baseDate = new Date(2024, 9, 7); // October is month 9 (zero-indexed)

      // Map day name to its corresponding offset from Monday (07/10/2024)
      const getDateForSelectedDay = (day) => {
          const dayMap = { "Monday": 0, "Tuesday": 1, "Wednesday": 2, "Thursday": 3, "Friday": 4 };
          const offset = dayMap[day];
          const selectedDate = new Date(baseDate);
          selectedDate.setDate(baseDate.getDate() + offset); // Add the offset to 07/10/2024 (Monday)
          return selectedDate;
      };

      // Prompt user to enter event details
      const form = await DayPilot.Modal.form([
          { name: "Event Name", id: "EventName", type: "text" },
          { name: "Classroom", id: "Classroom", type: "text" },
          { 
              name: "Day of the Week", 
              id: "Day", 
              type: "select", 
              options: [
                  { name: "Monday", id: "Monday" },
                  { name: "Tuesday", id: "Tuesday" },
                  { name: "Wednesday", id: "Wednesday" },
                  { name: "Thursday", id: "Thursday" },
                  { name: "Friday", id: "Friday" }
              ]
          },
          { name: "Start Time", id: "StartTime", type: "time" }, // Default start time
          { name: "End Time", id: "EndTime", type: "time" } // Default end time
      ], 
      { 
          EventName: "a",
          Classroom: "1",
          Day: args.day,
          StartTime: args.start.split('T')[1], // Extract time from args.start
          EndTime: args.end.split('T')[1] // Extract time from args.end
      });

      if (!form.result) {
          return; // If form is cancelled, do nothing
      }

      // Get the selected day and map it to a real date starting from 07/10/2024
      const selectedDay = form.result.Day;
      const selectedDate = getDateForSelectedDay(selectedDay);

      // Parse the selected start and end times
      const [startHour, startMinute] = form.result.StartTime.split(':').map(Number);
      const [endHour, endMinute] = form.result.EndTime.split(':').map(Number);

      // Create the full start and end dates with the selected times
      const startDate = new Date(selectedDate);
      startDate.setHours(startHour + 8, startMinute, 0, 0); // Adjust for timezone if needed

      const endDate = new Date(selectedDate);
      endDate.setHours(endHour + 8, endMinute, 0, 0); // Adjust for timezone if needed

      // Clear the selection on the calendar
      calendar.clearSelection();

      // Add the event to the calendar
      calendar.events.add({
          start: new DayPilot.Date(startDate), // Set start date and time
          end: new DayPilot.Date(endDate),     // Set end date and time
          id: DayPilot.guid(),                 // Generate a unique ID for the event
          text: form.result.EventName,         // Event name entered by the user
          classroom: form.result.Classroom     // Classroom entered by the user
      });
  },  

    onEventClick: async args => {
      await editEvent(args.e);
    },
    contextMenu: new DayPilot.Menu({
      items: [
        {
          text: "Delete",
          onClick: async args => {
            calendar.events.remove(args.source);
          },
        },
        {
          text: "-"
        },
        {
          text: "Edit...",
          onClick: async args => {
            await editEvent(args.source);
          }
        }
      ]
    }),
    onBeforeEventRender: args => {
      args.data.areas = [
        {
          top: 3,
          right: 3,
          width: 20,
          height: 20,
          symbol: "icons/daypilot.svg#minichevron-down-2",
          fontColor: "#fff",
          toolTip: "Show context menu",
          action: "ContextMenu",
        },
        {
          top: 3,
          right: 25,
          width: 20,
          height: 20,
          symbol: "icons/daypilot.svg#x-circle",
          fontColor: "#fff",
          action: "None",
          toolTip: "Delete event",
          onClick: async args => {
            calendar.events.remove(args.source);
          }
        }
      ];

      // Display classroom information visually in the event text
      args.data.html = `<div>
        <strong>${args.data.text}</strong><br />
        <span style="font-size: 12px; color: #FFF;">Classroom: ${args.data.classroom || 'N/A'}</span>
      </div>`;
    },
    onBeforeHeaderRender: args => {
      // Override header to only show the day names (Monday, Tuesday, etc.)
      const dayNames = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday"];
      const dayIndex = args.header.start.getDayOfWeek() - 1; // Monday is index 0
      args.header.html = dayNames[dayIndex];
    }
  };

  const editEvent = async (e) => {
  // Base reference date: 07/10/2024 is Monday
  const baseDate = new Date(2024, 9, 7); // October is month 9 (zero-indexed)

  // Map day name to its corresponding offset from Monday (07/10/2024)
  const getDateForSelectedDay = (day) => {
    const dayMap = { "Monday": 0, "Tuesday": 1, "Wednesday": 2, "Thursday": 3, "Friday": 4 };
    const offset = dayMap[day];
    const selectedDate = new Date(baseDate);
    selectedDate.setDate(baseDate.getDate() + offset); // Add the offset to 07/10/2024 (Monday)
    return selectedDate;
  };
    // Open modal form with dropdown for day selection and time inputs
    const form = await DayPilot.Modal.form([
      { 
        name: "Event Name", 
        id: "EventName", 
        type: "text", 
      },
      { 
        name: "Classroom", 
        id: "Classroom", 
        type: "text", 
      },
      { 
        name: "Day of the Week", 
        id: "Day", 
        type: "select", 
        options: [
          { name: "Monday", id: "Monday" },
          { name: "Tuesday", id: "Tuesday" },
          { name: "Wednesday", id: "Wednesday" },
          { name: "Thursday", id: "Thursday" },
          { name: "Friday", id: "Friday" }
        ], 
      },
      { 
        name: "Start Time", 
        id: "StartTime", 
        type: "time", 
      },
      { 
        name: "End Time", 
        id: "EndTime", 
        type: "time", 
      },
    ], {
      EventName: e.data.text,
      Classroom: e.data.classroom ,
      Start: e.data.start,
      End: e.data.end
    }
  );

    // If the form was canceled, stop the process
    if (!form.result) {
      return;
    }

    // Get the selected day and map it to a real date in the current week
    const selectedDay = form.result.Day;
    const selectedDate = getDateForSelectedDay(selectedDay);

    // Parse the selected time for start and end
    const [startHour, startMinute] = form.result.StartTime.split(':');
    const [endHour, endMinute] = form.result.EndTime.split(':');

    // Set the start and end time for the selected date
    const startDate = new Date(selectedDate);
    startDate.setHours(startHour, startMinute);

    const endDate = new Date(selectedDate);
    endDate.setHours(endHour, endMinute);

    // Update event data
    e.data.text = form.result.EventName;
    e.data.classroom = form.result.Classroom;
    e.data.start = form.result.Start;
    e.data.end = form.result.End;

    // Update the event in the calendar
    calendar.events.update(e);
  };


  useEffect(() => {
    const events = [
      {
        id: 1,
        text: "Event 1",
        start: "2024-10-07T10:30:00", // Monday
        end: "2024-10-07T13:00:00",
        classroom: "Classroom A", // Add classroom attribute here
      },
      {
        id: 2,
        text: "Event 2",
        start: "2024-10-08T09:30:00", // Tuesday
        end: "2024-10-08T11:30:00",
        classroom: "Classroom B", // Add classroom attribute here
        backColor: "#6aa84f",
      },
      {
        id: 3,
        text: "Event 3",
        start: "2024-10-09T12:00:00", // Wednesday
        end: "2024-10-09T15:00:00",
        classroom: "Classroom C", // Add classroom attribute here
        backColor: "#f1c232",
      },
      {
        id: 4,
        text: "Event 4",
        start: "2024-10-10T11:30:00", // Thursday
        end: "2024-10-10T14:30:00",
        classroom: "Classroom D", // Add classroom attribute here
        backColor: "#cc4125",
      },
      {
        id: 5,
        text: "Event 5",
        start: "2024-10-11T10:00:00", // Friday
        end: "2024-10-11T12:00:00",
        classroom: "Classroom E", // Add classroom attribute here
      }
    ];
    setEvents(events);
  }, []);

  return (

    <div style={styles.wrap}>
      <div style={styles.main}>
        <DayPilotCalendar
          {...config}
          events={events}
          startDate={startDate}
          controlRef={setCalendar}
          style={{ height: "1000px", width: "100%" }}
        />
      </div>
    </div>
  );
}

export default Calendar;
