import React, { useEffect, useState } from 'react';
import { DayPilotCalendar, DayPilot } from "@daypilot/daypilot-lite-react"; // Import DayPilot
import "./Calendar.css";

const styles = {
  wrap: {
    display: "flex"
  },
  main: {
    flexGrow: "1"
  }
};

const Calendar = () => {
  const [calendar, setCalendar] = useState(null);
  const [events, setEvents] = useState([]);

  // Fixed start date to always show the same week (Monday to Friday)
  const startDate = "2025-10-06"; // Monday of the desired week

  const config = {
    viewType: "Week",
    durationBarVisible: false,
    timeRangeSelectedHandling: "Enabled",
    onTimeRangeSelected: async args => {
      // Prompt user to enter both event text and classroom
      const eventText = await DayPilot.Modal.prompt("Create a new event:", "Event 1");
      const classroom = await DayPilot.Modal.prompt("Enter classroom for this event:", "Classroom 101");

      calendar.clearSelection();
      if (!eventText.result || !classroom.result) { return; }

      calendar.events.add({
        start: args.start,
        end: args.end,
        id: DayPilot.guid(),
        text: eventText.result,
        classroom: classroom.result // Add classroom attribute here
      });
    },
    onEventClick: async args => {
      await editEvent(args.e);
    },
    contextMenu: new DayPilot.Menu({
      items: [
        {
          text: "Delete",
          onClick: async args => {
            calendar.events.remove(args.source);
          },
        },
        {
          text: "-"
        },
        {
          text: "Edit...",
          onClick: async args => {
            await editEvent(args.source);
          }
        }
      ]
    }),
    onBeforeEventRender: args => {
      args.data.areas = [
        {
          top: 3,
          right: 3,
          width: 20,
          height: 20,
          symbol: "icons/daypilot.svg#minichevron-down-2",
          fontColor: "#fff",
          toolTip: "Show context menu",
          action: "ContextMenu",
        },
        {
          top: 3,
          right: 25,
          width: 20,
          height: 20,
          symbol: "icons/daypilot.svg#x-circle",
          fontColor: "#fff",
          action: "None",
          toolTip: "Delete event",
          onClick: async args => {
            calendar.events.remove(args.source);
          }
        }
      ];

      // Display classroom information visually in the event text
      args.data.html = `<div>
        <strong>${args.data.text}</strong><br />
        <span style="font-size: 12px; color: #666;">Classroom: ${args.data.classroom || 'N/A'}</span>
      </div>`;
    }
  };

  const editEvent = async (e) => {
    // Prompt to edit both the event text and the classroom
    const eventText = await DayPilot.Modal.prompt("Update event text:", e.text());
    const classroom = await DayPilot.Modal.prompt("Update classroom:", e.data.classroom || "Classroom 101");

    if (!eventText.result || !classroom.result) { return; }

    e.data.text = eventText.result;
    e.data.classroom = classroom.result; // Update classroom
    calendar.events.update(e);
  };

  useEffect(() => {
    const events = [
      {
        id: 1,
        text: "Event 1",
        start: "2025-10-06T10:30:00", // Monday
        end: "2025-10-06T13:00:00",
        classroom: "Classroom A", // Add classroom attribute here
        participants: 2,
      },
      {
        id: 2,
        text: "Event 2",
        start: "2025-10-07T09:30:00", // Tuesday
        end: "2025-10-07T11:30:00",
        classroom: "Classroom B", // Add classroom attribute here
        backColor: "#6aa84f",
        participants: 1,
      },
      {
        id: 3,
        text: "Event 3",
        start: "2025-10-08T12:00:00", // Wednesday
        end: "2025-10-08T15:00:00",
        classroom: "Classroom C", // Add classroom attribute here
        backColor: "#f1c232",
      },
      {
        id: 4,
        text: "Event 4",
        start: "2025-10-09T11:30:00", // Thursday
        end: "2025-10-09T14:30:00",
        classroom: "Classroom D", // Add classroom attribute here
        backColor: "#cc4125",
      },
      {
        id: 5,
        text: "Event 5",
        start: "2025-10-10T10:00:00", // Friday
        end: "2025-10-10T12:00:00",
        classroom: "Classroom E", // Add classroom attribute here
      }
    ];
    setEvents(events);
  }, []);

  return (
    <div style={styles.wrap}>
      <div style={styles.main}>
        <DayPilotCalendar
          {...config}
          events={events}
          startDate={startDate}
          controlRef={setCalendar}
        />
      </div>
    </div>
  );
}

export default Calendar;
