import React, { useEffect, useState } from 'react';
import { DayPilotCalendar, DayPilot } from "@daypilot/daypilot-lite-react"; // Import DayPilot
import "./Calendar.css";
import { convertToDayDateTime, processFormDates, convertToCSV, downloadCSV, getDateForSelectedDay } from './days.js';

const styles = {
  wrap: {
    display: "flex"
  },
  main: {
    flexGrow: "1"
  }
};

const Calendar = () => {
  const [calendar, setCalendar] = useState(null);
  const [events, setEvents] = useState([]);

  const startDate = "2024-10-07"; // Fixed start date for the week (Monday)

  const config = {
    viewType: "days",
    durationBarVisible: false,
    days: 5, // Show only Monday to Friday
    timeRangeSelectedHandling: "Enabled",
    businessBeginsHour: 7,
    businessEndsHour: 20,
    // Time range selection and event creation logic goes here...
    events: events
  };

  // Helper function to read the CSV file and parse it into event objects
  const parseCSVFile = (file) => {
    const reader = new FileReader();
    reader.onload = (e) => {
      const csvContent = e.target.result;
      console.log("CSV Content:", csvContent); // Log the CSV content for debugging

      // Split rows and remove any empty lines
      const rows = csvContent.split("\n").filter(row => row.trim() !== "");

      const parsedEvents = rows.map((row, index) => {
        // Remove the trailing '\r' from each row and split it into columns
        const columns = row.replace(/\r$/, "").split(",").map(col => col.trim());

        // Check if we have the expected number of columns
        if (columns.length < 8) {
          console.error(`Row ${index + 1} has missing columns`, columns);
          return null; // Skip this row
        }

        const [module, classInfo, classType, timeSlot, room, timeslotDay, timeslotStart, timeslotEnd] = columns;

        // Skip rows where timeslotStart or timeslotEnd is missing or invalid
        if (!timeslotStart || !timeslotEnd || timeslotStart.trim() === "" || timeslotEnd.trim() === "") {
          console.error(`Invalid timeslot data on row ${index + 1}:`, { timeslotStart, timeslotEnd });
          return null; // Skip this row
        }

        // Process start and end times
        const { startDateTime, endDateTime } = processFormDates(timeslotDay, timeslotStart.trim(), timeslotEnd.trim());

        console.log({
          id: DayPilot.guid(),
          start: new DayPilot.Date(startDateTime),
          end: new DayPilot.Date(endDateTime),
          text: module,
          classroom: room,
          class: classInfo,
          classType: classType
        })

        return {
          id: DayPilot.guid(),
          start: new DayPilot.Date(startDateTime),
          end: new DayPilot.Date(endDateTime),
          text: module,
          classroom: room,
          class: classInfo,
          classType: classType
        };

      }).filter(event => event !== null); // Filter out invalid rows

      setEvents(parsedEvents); // Update the events state
      calendar.update(); // Force the calendar to update
    };

    reader.readAsText(file); // Read the CSV file as text
  };



  return (
    <div>
      <div style={styles.buttonWrap}>

        {/* Add the file input for CSV import */}
        <input
          type="file"
          accept=".csv"
          onChange={(e) => {
            const file = e.target.files[0];
            if (file) {
              parseCSVFile(file);
            }
          }}
        />

      <button onClick={() => {
          const csvContent = convertToCSV(events);  // Convert events to CSV format
          downloadCSV(csvContent, 'schedule.csv');  // Trigger the download
        }}>
          Export to CSV
        </button>
        {/* <button>Import from CSV</button> */}
      </div>

      <div style={styles.main}>
        <DayPilotCalendar
          {...config}
          startDate={startDate}
          controlRef={setCalendar}
        />
      </div>
    </div>
  );
}

export default Calendar;
